- (void)testExample {
    NSData *message = [@"" hexToData];
    BTTx *tx = [BTTx transactionWithMessage:message];
    XCTAssert(tx != nil);

    NSArray *myPathList = NSSearchPathForDirectoriesInDomains(NSDocumentDirectory, NSUserDomainMask, YES);
    NSString *myPath = myPathList[0];
    myPath = [myPath stringByAppendingPathComponent:@"1C6FiRktL3UPd4sywhyU5CYSeLdKhvHxhR.tx"];
    NSArray *lines2 = [[NSString stringWithContentsOfFile:myPath encoding:NSUTF8StringEncoding error:nil] componentsSeparatedByString:@"\n"];
    NSArray *lines = [rawTxs componentsSeparatedByString:@"\n"];
    XCTAssert(25 == [lines count]);
    for (NSString *each in lines2) {
        BTTx *tx1 = [BTTx transactionWithMessage:[each hexToData]];
        XCTAssert([[[tx1 toData] SHA256_2] isEqualToData:tx1.txHash]);
        XCTAssert([tx1 verifySignatures]);
        [[BTTxProvider instance] add:tx1];
        BTTx *tx2 = [[BTTxProvider instance] getTxDetailByTxHash:tx1.txHash];
        XCTAssert([[[tx2 toData] SHA256_2] isEqualToData:tx2.txHash]);
        XCTAssert([tx1 isEqual:tx2]);

        tx1.txVer += 1;
        XCTAssert(![[[tx1 toData] SHA256_2] isEqualToData:tx1.txHash]);
//    XCTAssert(tx1.isSigned);
    }

}

- (void)testSignature;{
    BTTx *tx = [BTTx transactionWithMessage:[@"" hexToData]];
    BTTx *prevTx = [BTTx transactionWithMessage:[@"" hexToData]];

    BOOL valid = YES;
    for (NSUInteger i = 0; i < [self getInIndexes:tx].count; i++) {
        if ([[self getInHashes:tx][i] isEqualToData:prevTx.txHash]) {
            if ([[self getInIndexes:tx][i] unsignedIntValue] < prevTx.outs.count) {
                NSData *outScript = ((BTOut *)prevTx.outs[[[self getInIndexes:tx][i] unsignedIntValue]]).outScript;
                BTScript *pubKeyScript = [[BTScript alloc] initWithProgram:outScript];
                BTScript *script = [[BTScript alloc] initWithProgram:((BTIn *)tx.ins[i]).inSignature];
                script.tx = tx;
                script.index = i;
                valid &= [script correctlySpends:pubKeyScript and:YES];
            } else {
                valid = NO;
            }
            if (!valid)
                break;
        }
    }
    XCTAssert(valid);

    BTTx *bip16Tx1 = [BTTx transactionWithMessage:[@"" hexToData]];
    BTTx *bip16Tx2 = [BTTx transactionWithMessage:[@"" hexToData]];
    NSArray *outAddresses = [self getOutAddresses:bip16Tx1];

    BTTx *tx1 = [BTTx transactionWithMessage:[@"" hexToData]];
    BTTx *tx2 = [BTTx transactionWithMessage:[@"" hexToData]];

    BTScript *sigScript = [[BTScript alloc] initWithProgram:((BTIn *)tx1.ins[0]).inSignature];
    sigScript.tx = tx1;
    sigScript.index = 0;
    BTScript *pubKeyScript = [[BTScript alloc] initWithProgram:((BTOut *)tx2.outs[1]).outScript];
    BOOL result = [sigScript correctlySpends:pubKeyScript and:YES];
}

- (NSArray *)getInHashes:(BTTx *) tx {
    NSMutableArray *result = [NSMutableArray new];
    for (BTIn *in in tx.ins) {
        [result addObject:in.prevTxHash];
    }
    return result;
}

- (NSArray *)getInIndexes:(BTTx *) tx {
    NSMutableArray *result = [NSMutableArray new];
    for (BTIn *in in tx.ins) {
        [result addObject:@(in.prevOutSn)];
    }
    return result;
}

- (NSArray *)getOutAmounts:(BTTx *) tx {
    NSMutableArray *result = [NSMutableArray new];
    for (BTOut *out in tx.outs) {
        [result addObject:@(out.outValue)];
    }
    return result;
}

- (NSArray *)getOutAddresses:(BTTx *) tx {
    NSMutableArray *result = [NSMutableArray new];
    for (BTOut *out in tx.outs) {
        [result addObject:out.outAddress ?: [NSNull null]];
    }
    return result;
}

